import { useEffect, useState } from "react";
import { dailyProps } from "./WeatherProps";

function WeatherDailyCall({ latIn, lonIn }: any) {
    const [result, setResult] = useState<dailyProps[]>([]);

    useEffect(() => {
        const url = "http://api.openweathermap.org/data/3.0/onecall?";
        const lat = `${latIn}`;
        const lon = `${lonIn}`;
        const appid = "8cd20943bde0b48ee5d7a9b271ac1f43";
        const units = "metric";
        const lang = "en";

        const apiCall = async () => {
            const data =
                await fetch(
                    `${url}lat=${lat}&lon=${lon}&appid=${appid}&units=${units}&lang=${lang}`,
                    { method: "GET" }
                );
            const weatherData = await data.json();
            setResult(weatherData.daily);
        };

        apiCall();
    }, [latIn, lonIn]);

    return (result);
};

export default WeatherDailyCall;
